import { ICurrentUser, IAction } from './userReducer';
import { UserActionTypes } from './userActionTypes';

export const setCurrentUser: (user: ICurrentUser | null) => IAction = (
  user: ICurrentUser | null
) => ({
  type: UserActionTypes.SET_CURRENT_USER,
  payload: user,
});

export const googleSignInStart: () => IAction = () => ({
  type: UserActionTypes.GOOGLE_SIGN_IN_START,
});

export const googleSignInSuccess: (user: ICurrentUser) => IAction = (
  user: ICurrentUser
) => ({
  type: UserActionTypes.GOOGLE_SIGN_IN_SUCCESS,
  payload: user,
});

export const googleSignInFailure: (error: Error) => IAction = (
  error: Error
) => ({
  type: UserActionTypes.GOOGLE_SIGN_IN_FAILURE,
  payload: error.message,
});

export const emailSignInStart: ({
  email: string,
  password: string,
}) => IAction = (emailAndPassword: object) => ({
  type: UserActionTypes.EMAIL_SIGN_IN_START,
  payload: emailAndPassword,
});

export const emailSignInSuccess: (user: ICurrentUser) => IAction = (
  user: ICurrentUser
) => ({
  type: UserActionTypes.EMAIL_SIGN_IN_SUCCESS,
  payload: user,
});

export const emailSignInFailure: (error: Error) => IAction = (
  error: Error
) => ({
  type: UserActionTypes.EMAIL_SIGN_IN_FAILURE,
  payload: error.message,
});
