import React from 'react';
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import CollectionItem from '../../components/CollectionItem/CollectionItem';
import { RootState } from '../../redux/rootReducer';
import { ICollection } from '../../redux/shop/shopReducer';
import './CollectionPage.style.scss';

const COLLECTION_ID_MAP = {
  hats: 1,
  sneakers: 2,
  jackets: 3,
  womens: 4,
  mens: 5,
};

interface RouteParams {
  collectionId: string;
}

const CollectionPage: React.FC = () => {
  const params = useParams<RouteParams>();
  const shopData = useSelector<RootState, ICollection[]>((state) => state.shop);
  if (shopData.length === 0) {
    return;
  }
  if (params.collectionId in Object.keys(COLLECTION_ID_MAP)) {
    const collectionData = shopData.filter(
      (item) => item.id === COLLECTION_ID_MAP[params.collectionId]
    );
  }

  return (
    <div className="category">
      <h2>collection page</h2>
    </div>
  );
};

export default CollectionPage;
