import { ShopActionTypes } from './shopActionTypes';
import { ICollection } from './shopReducer';

export const updateCollections = (collections: {
  [key: string]: ICollection;
}) => ({
  type: ShopActionTypes.UPDATE_COLLECTIONS,
  payload: collections,
});
