import { all, call } from 'redux-saga/effects';
import { fetchCollectionsStart } from './shop/shopSagas';
import { userSagas } from './user/userSaga';

export function* rootSaga() {
  yield all([call(fetchCollectionsStart), call(userSagas)]);
}
